import Head from 'next/head';
import Link from 'next/link';
import Layout from '../../components/layout';
import { useState } from 'react';
import { listBlogsWithCategoriesAndTags } from '../../actions/blog';
import { API } from '../../config';
import moment from 'moment';

const Card=({blog})=>{
    const showBlogCategories = blog =>  

    blog.categories.map((c, i) => (
        <Link key={i} href={`/categories/${c.slug}`}>
            <a className="btn btn-primary mr-1 ml-1 mt-3">{c.name}</a>
        </Link>
    ));


const showBlogTags = blog =>

    blog.tags.map((t, i) => (
        <Link key={i} href={`/tags/${t.slug}`}>
            <a className="btn btn-outline-primary mr-1 ml-1 mt-3">{t.name}</a>
        </Link>
    ));

    return (
        <div className="lead pb-4">
        <header>
            <Link href={`/blogs/${blog.slug}`}>
                <a>
                    <h2 className="pt-3 pb-3 font-weight-bold">{blog.title}</h2>
                </a>
            </Link>
        </header>
        <section>
            <p className="mark ml-1 pt-2 pb-2">
                Written by   <Link href={`/profile/${blog.postedBy.username}`}>
                    <a className="float-right">{blog.postedBy.username}</a>
                </Link> | Published {moment(blog.updatedAt).fromNow()}
            </p>
        </section>
        <section>
        {showBlogCategories(blog)}
                {showBlogTags(blog)}
                <br />
                <br />
        </section>

        <div className="row">
            <div className="col-md-4">  <section>
                        <img
                            className="img img-fluid"
                            style={{ maxHeight: '150px', width: 'auto' }}
                            src={`${API}/api/blog/photo/${blog.slug}`}
                            alt={blog.title}
                        />
                    </section></div>
            <div className="col-md-8">
                <section>
                    <div className="pb-3">{(blog.excerpt)}</div>
                    <Link href={`/blogs/${blog.slug}`}>
                        <a className="btn btn-primary pt-2">Read more</a>
                    </Link>
                </section>
            </div>
        </div>
    </div>
    )
}

export default Card;
